pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title IssuerRegistry
 * @dev Contract to manage authorized SBT issuers
 * @author SBT System POC
 */
contract IssuerRegistry is Ownable, Pausable {
    struct IssuerInfo {
        string name;
        string organization;
        bool authorized;
        uint256 addedAt;
    }

    // Mapping from issuer address to issuer info
    mapping(address => IssuerInfo) private issuers;

    // Array to track all issuer addresses for enumeration
    address[] public issuerAddresses;

    // Mapping to track if address is in the array (for gas optimization)
    mapping(address => bool) private isInArray;

    // Events
    event IssuerAdded(
        address indexed issuer,
        string name,
        string organization,
        uint256 timestamp
    );

    event IssuerRemoved(
        address indexed issuer,
        uint256 timestamp
    );

    event IssuerUpdated(
        address indexed issuer,
        string name,
        string organization,
        uint256 timestamp
    );

    constructor(address _owner) Ownable(_owner) {
        // Add the owner as the first authorized issuer
        _addIssuerInternal(_owner, "System Admin", "SBT System");
    }

    /**
     * @dev Add a new authorized issuer
     * @param issuer Address of the issuer
     * @param name Name of the issuer
     * @param organization Organization name
     */
    function addIssuer(
        address issuer,
        string memory name,
        string memory organization
    ) external onlyOwner whenNotPaused {
        require(issuer != address(0), "IssuerRegistry: Invalid issuer address");
        require(bytes(name).length > 0, "IssuerRegistry: Name cannot be empty");
        require(!issuers[issuer].authorized, "IssuerRegistry: Issuer already exists");

        _addIssuerInternal(issuer, name, organization);
    }

    /**
     * @dev Internal function to add issuer
     */
    function _addIssuerInternal(
        address issuer,
        string memory name,
        string memory organization
    ) internal {
        issuers[issuer] = IssuerInfo({
            name: name,
            organization: organization,
            authorized: true,
            addedAt: block.timestamp
        });

        if (!isInArray[issuer]) {
            issuerAddresses.push(issuer);
            isInArray[issuer] = true;
        }

        emit IssuerAdded(issuer, name, organization, block.timestamp);
    }

    /**
     * @dev Remove an authorized issuer
     * @param issuer Address of the issuer to remove
     */
    function removeIssuer(address issuer) external onlyOwner whenNotPaused {
        require(issuer != address(0), "IssuerRegistry: Invalid issuer address");
        require(issuers[issuer].authorized, "IssuerRegistry: Issuer not found");
        require(issuer != owner(), "IssuerRegistry: Cannot remove owner");

        issuers[issuer].authorized = false;

        emit IssuerRemoved(issuer, block.timestamp);
    }

    /**
     * @dev Update issuer information
     * @param issuer Address of the issuer
     * @param name New name
     * @param organization New organization
     */
    function updateIssuer(
        address issuer,
        string memory name,
        string memory organization
    ) external onlyOwner whenNotPaused {
        require(issuer != address(0), "IssuerRegistry: Invalid issuer address");
        require(issuers[issuer].authorized, "IssuerRegistry: Issuer not found");
        require(bytes(name).length > 0, "IssuerRegistry: Name cannot be empty");

        issuers[issuer].name = name;
        issuers[issuer].organization = organization;

        emit IssuerUpdated(issuer, name, organization, block.timestamp);
    }

    /**
     * @dev Check if an address is an authorized issuer
     * @param issuer Address to check
     * @return bool True if authorized, false otherwise
     */
    function isAuthorizedIssuer(address issuer) external view returns (bool) {
        return issuers[issuer].authorized;
    }

    /**
     * @dev Get issuer information
     * @param issuer Address of the issuer
     * @return IssuerInfo struct containing issuer details
     */
    function getIssuerInfo(address issuer) external view returns (IssuerInfo memory) {
        return issuers[issuer];
    }

    /**
     * @dev Get all authorized issuer addresses
     * @return address[] Array of all issuer addresses
     */
    function getAllIssuers() external view returns (address[] memory) {
        uint256 authorizedCount = 0;

        // Count authorized issuers
        for (uint256 i = 0; i < issuerAddresses.length; i++) {
            if (issuers[issuerAddresses[i]].authorized) {
                authorizedCount++;
            }
        }

        // Create array of authorized issuers
        address[] memory authorizedIssuers = new address[](authorizedCount);
        uint256 index = 0;

        for (uint256 i = 0; i < issuerAddresses.length; i++) {
            if (issuers[issuerAddresses[i]].authorized) {
                authorizedIssuers[index] = issuerAddresses[i];
                index++;
            }
        }

        return authorizedIssuers;
    }

    /**
     * @dev Get total count of authorized issuers
     * @return uint256 Count of authorized issuers
     */
    function getAuthorizedIssuerCount() external view returns (uint256) {
        uint256 count = 0;
        for (uint256 i = 0; i < issuerAddresses.length; i++) {
            if (issuers[issuerAddresses[i]].authorized) {
                count++;
            }
        }
        return count;
    }

    /**
     * @dev Pause the contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}","keccak256":"0x2ff2ba6a3a1cc897ce7bc3452860e0ad8b4f75829100e34d867b694dacdc976b","license":"MIT"},"contracts/SBTTemplate.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./IssuerRegistry.sol";

/**
 * @title SBTTemplate
 * @dev Contract for managing SBT templates that can be used to mint tokens
 */
contract SBTTemplate is Ownable, Pausable {
    struct Template {
        uint256 templateId;
        string name;
        string description;
        address issuer;
        uint256 createdAt;
        bool active;
    }

    // State variables
    uint256 private _templateIdCounter;
    mapping(uint256 => Template) public templates;
    mapping(address => uint256[]) public issuerTemplates;

    IssuerRegistry public immutable issuerRegistry;

    // Events
    event TemplateCreated(
        uint256 indexed templateId,
        address indexed issuer,
        string name,
        string description
    );

    event TemplateUpdated(
        uint256 indexed templateId,
        string name,
        string description
    );

    event TemplateDeactivated(uint256 indexed templateId);
    event TemplateReactivated(uint256 indexed templateId);

    // Modifiers
    modifier onlyAuthorizedIssuer() {
        require(
            issuerRegistry.isAuthorizedIssuer(msg.sender),
            "SBTTemplate: caller is not an authorized issuer"
        );
        _;
    }

    modifier templateExists(uint256 templateId) {
        require(templateId <= _templateIdCounter && templateId > 0, "SBTTemplate: template does not exist");
        _;
    }

    modifier onlyTemplateIssuer(uint256 templateId) {
        require(templates[templateId].issuer == msg.sender, "SBTTemplate: not template issuer");
        _;
    }

    constructor(address _issuerRegistry) Ownable(msg.sender) {
        require(_issuerRegistry != address(0), "SBTTemplate: invalid issuer registry address");
        issuerRegistry = IssuerRegistry(_issuerRegistry);
        _templateIdCounter = 0;
    }

    /**
     * @dev Create a new SBT template
     * @param name The name for the SBT template
     * @param description The description for the SBT template
     * @return templateId The ID of the created template
     */
    function createTemplate(
        string memory name,
        string memory description
    ) external onlyAuthorizedIssuer whenNotPaused returns (uint256) {
        require(bytes(name).length > 0, "SBTTemplate: name cannot be empty");
        require(bytes(description).length > 0, "SBTTemplate: description cannot be empty");

        _templateIdCounter++;
        uint256 templateId = _templateIdCounter;

        templates[templateId] = Template({
            templateId: templateId,
            name: name,
            description: description,
            issuer: msg.sender,
            createdAt: block.timestamp,
            active: true
        });

        issuerTemplates[msg.sender].push(templateId);

        emit TemplateCreated(templateId, msg.sender, name, description);

        return templateId;
    }

    /**
     * @dev Update an existing template
     * @param templateId The ID of the template to update
     * @param name The new name for the template
     * @param description The new description for the template
     */
    function updateTemplate(
        uint256 templateId,
        string memory name,
        string memory description
    ) external templateExists(templateId) onlyTemplateIssuer(templateId) whenNotPaused {
        require(bytes(name).length > 0, "SBTTemplate: name cannot be empty");
        require(bytes(description).length > 0, "SBTTemplate: description cannot be empty");
        require(templates[templateId].active, "SBTTemplate: template is not active");

        templates[templateId].name = name;
        templates[templateId].description = description;

        emit TemplateUpdated(templateId, name, description);
    }

    /**
     * @dev Deactivate a template
     * @param templateId The ID of the template to deactivate
     */
    function deactivateTemplate(uint256 templateId)
        external
        templateExists(templateId)
        onlyTemplateIssuer(templateId)
        whenNotPaused
    {
        require(templates[templateId].active, "SBTTemplate: template already inactive");

        templates[templateId].active = false;
        emit TemplateDeactivated(templateId);
    }

    /**
     * @dev Reactivate a template
     * @param templateId The ID of the template to reactivate
     */
    function reactivateTemplate(uint256 templateId)
        external
        templateExists(templateId)
        onlyTemplateIssuer(templateId)
        whenNotPaused
    {
        require(!templates[templateId].active, "SBTTemplate: template already active");

        templates[templateId].active = true;
        emit TemplateReactivated(templateId);
    }

    /**
     * @dev Get template details
     * @param templateId The ID of the template
     * @return Template struct with all template data
     */
    function getTemplate(uint256 templateId)
        external
        view
        templateExists(templateId)
        returns (Template memory)
    {
        return templates[templateId];
    }

    /**
     * @dev Get all template IDs for an issuer
     * @param issuer The address of the issuer
     * @return Array of template IDs
     */
    function getIssuerTemplates(address issuer) external view returns (uint256[] memory) {
        return issuerTemplates[issuer];
    }

    /**
     * @dev Get total number of templates created
     * @return The total template count
     */
    function getTotalTemplates() external view returns (uint256) {
        return _templateIdCounter;
    }

    /**
     * @dev Check if a template is active and exists
     * @param templateId The ID of the template
     * @return Boolean indicating if template is active
     */
    function isTemplateActive(uint256 templateId) external view returns (bool) {
        if (templateId > _templateIdCounter || templateId == 0) {
            return false;
        }
        return templates[templateId].active;
    }

    // Admin functions
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }
}","keccak256":"0x7677115b228ccfea1bf5bff2849b682bfd7f1d6c4f375a16b82d5cffae4f3569","license":"MIT"}},"version":1}
